import Button from './Button/Button';
import React from 'react';
import './Input.css';

const input = (props) => {
    handleKeyDown = (e) => {
        if (e.key === 'Enter') {
            console.log('do validate');
        }
    }

    return (
        <div className = "Input">
            <input className="TextInput" text="text" value={props.textInput} onChange={props.onChangeHandler} onKeyDown={() => handleKeyDown(props.addItem)} ></input>
            <Button clicked={props.addItem}/>
        </div>
    )
}

export default input;
