import React, {Component} from 'react';
import {withRouter} from 'react-router-dom';
import Task from './Task/Task';

import Input from '../Input/Input';

class Tasks extends Component {
    render(){
        const completedTask = {
            opacity: '0.5'
        }
        const listIndex = this.props.match.params.listName;
        
        return(
            <React.Fragment>
                <p className="tasksTitle">Todo ({this.props.list.inprogressTasks.length})</p>
                {this.props.list.inprogressTasks.map((item, key) => {
                    return(<Task
                        style={{opacity: '1'}}
                        key={key}
                        name={item}
                        clicked={() => this.props.completeTask(key, listIndex)}
                        deleteTask={() => this.props.deleteTask(key, true, listIndex)}
                    />);
                })}

                <br />
                <br />

                <h3>Completed ({this.props.list.completedTasks.length})</h3>
                {this.props.list.completedTasks.map((item, key) => {
                    return(<Task
                        style={completedTask}
                        key={key} 
                        name={item} 
                        clicked={() => this.props.incompleteTask(key, listIndex)}
                        deleteTask={() => this.props.deleteTask(key, false, listIndex)}
                    />);
                })}

                <Input addItem={()=>this.props.addTask(listIndex)} onChangeHandler={this.props.onChangeHandler} textInput={this.props.taskInput} />
            </React.Fragment>
        )
    }
}

export default withRouter(Tasks);
