import Button from './Button/Button';
import React, {Component} from 'react';
import './Input.css';

class Input extends Component {
    handleKeyDown = (e) => {
        if (e.key === 'Enter') {
            this.props.addItem();
        }
    }

    render(){
        return (
            <div className = "Input">
                <input className="TextInput" text="text" placeholder={props.placeHolderText} value={this.props.textInput} onChange={this.props.onChangeHandler} onKeyDown={this.handleKeyDown} ></input>
                <Button clicked={this.props.addItem}/>
            </div>
        )
    }
}

export default Input;
