import React, {Component} from 'react';
import './Lists.css';
import List from './List/List';
import Input from '../Input/Input';

class Lists extends Component {
    selectList = (id) => {
        let list;
        
        for(let i = 0; i < this.props.lists.length; i ++){
            if(this.props.lists[i].title === id){
                list = this.props.lists[i];
            }
        }

        console.log(list + "THIS IS IS")
        this.props.history.push({pathname: '/lists/' + id, state: list});
        //this.props.history.push( '/lists/' + id );
    }

    render(){
        return (
            <React.Fragment>
                <div className = "Lists">
                    {this.props.lists.map((list, key)=> {
                        return (
                        <List key={key} 
                            clicked={() => this.selectList(list.title)} 
                            list={list}
                        />)
                    })}
                </div>
                <Input clicked={this.props.addList}/>
            </React.Fragment>
        )
    }
}

export default Lists;
