        //this.props.history.push( '/lists/' + id );
    } */

    selectList = (listName) => {
        this.props.history.push( '/lists/' + listName );
    }

    render(){
        return (
            <React.Fragment>
                <div className = "Lists">
                    {this.props.lists.map((list, key)=> {
                        return (
                        <List
                            key={key} 
                            clicked={() => this.selectList(list.listName)} 
                            list={list}
                        />)
                    })}
                </div>
                {/* <Input clicked={this.props.addList}/> */}
            </React.Fragment>
        )
    }
}

export default withRouter(Lists);
