import React from 'react';
import {Link} from 'react-router-dom';
import './Header.css';

const header = (props) => {
    let backLink;
    if(props.pageTitle != "Lists"){
        backLink = <Link className="ReturnLink" to="/lists" onClick={props.updateTitle("Lists")}>Return to lists</Link>
    }
    return (
        <div className = "Header">
            {backLink}
            <h3>{props.pageTitle}</h3>
        </div>
    )
}

export default header;
