import React, {Component} from 'react';
import {Route, Switch, Redirect} from 'react-router-dom';
import './App.css';
import Footer from '../components/Footer/Footer';
import Header from '../components/Header/Header';
import Tasks from '../components/Tasks/Tasks';
import Lists from '../components/Lists/Lists';

class App extends Component {
  state = {
    lists: [
      {
        listName: "Get to work",
        inprogressTasks: [
          "Do Math questions",
          "Leet code problem"
        ],
        completedTasks: [
          "Add lists to React project"
        ]
      },
      {
        listName: "Gym",
        inprogressTasks: [
          "Eat hype pre meal",
          "Go to Gym"
        ],
        completedTasks: [
          "Task 3"
        ]
      }
    ],
    taskInput: "",
    listInput: "",
    currentPageTitle: ""
  }

  updatePageTitle = (pageTitle) => {
    const updatedPageTitle = this.state.pageTitle;
    updatedPageTitle = pageTitle;
    this.setState({pageTitle: updatedPageTitle});
  }

  onChangeTaskInputHandler = (event) => {
    const updatedTaskInput = event.target.value;
    this.setState({taskInput: updatedTaskInput})
  }

  onChangeListInputHandler = (event) => {
    const updatedListInput = event.target.value;
    this.setState({listInput: updatedListInput})
  }

  completeTask = (taskIndex, listIndex) => {
    const updatedList = this.state.lists;
    updatedList[listIndex].completedTasks.push(updatedList[listIndex].inprogressTasks[taskIndex])
    updatedList[listIndex].inprogressTasks.splice(taskIndex, 1);
    this.setState({lists: updatedList})
  }

  addTask = (listIndex) => {
    if(/\S/.test(this.state.taskInput)){
      const updatedList = this.state.lists;
      updatedList[listIndex].inprogressTasks.push(this.state.taskInput);
      this.setState({lists: updatedList});
      this.setState({taskInput: ""});
    }
  }

  incompleteTask = (taskIndex, listIndex) => {
    const updatedList = this.state.lists;
    updatedList[listIndex].inprogressTasks.push(updatedList[listIndex].completedTasks[taskIndex])
    updatedList[listIndex].completedTasks.splice(taskIndex, 1);
    this.setState({lists: updatedList})
  }

  deleteTask = (taskIndex, inprogress, listIndex) => {
    const updatedList = this.state.lists;
    if(inprogress){
      updatedList[listIndex].inprogressTasks.splice(taskIndex, 1);
    }
    else {
      updatedList[listIndex].completedTasks.splice(taskIndex, 1);
    }
    
    this.setState({lists: updatedList})
  }


  addList = () => {
    if(/\S/.test(this.state.listInput)){
      const updatedLists = this.state.lists;
      const newList = {
        listName: this.state.listInput,
        inprogressTasks: [],
        completedTasks: []
      }
      updatedLists.push(newList)

      this.setState({lists: updatedLists});
      this.setState({listInput: ""});
    }
  }

  render(){
    return (
        <div className="App">
            <Header pageTitle={this.state.currentPageTitle}/>
            <Switch>

            <Route path="/lists/:listName" render={(props) => <Tasks
                {...props}
                list={this.state.lists[props.match.params.listName]}
                addTask={this.addTask}
                completeTask={this.completeTask}
                incompleteTask={this.incompleteTask}
                deleteTask={this.deleteTask}
                onChangeHandler={this.onChangeTaskInputHandler}
                taskInput={this.state.taskInput}
              />} />

              <Route path="/lists" exact render={()=> <Lists
                lists={this.state.lists}
                addList={this.addList}
                onChangeHandler={this.onChangeListInputHandler}
                textInput={this.state.listInput} />}
                updatePageTitle={this.updatePageTitle}
              />

              <Redirect from="/" to="/lists" />
              <Route render={()=> <p>404 Not Found :(</p>} />

            </Switch>
            <Footer />
        </div>
    );
  }
}

export default App;
