import React, {Component} from 'react';
import './Lists.css';
import List from './List/List';
import Input from '../Input/Input';
import {Link, withRouter} from 'react-router-dom';

class Lists extends Component {
    updateTitle = (list) => {
        this.props.updatePageTitle(list);
    }

    componentDidMount(){
        const updateTitle = this.props.updatePageTitle;
        updateTitle("Lists");
    }

    render(){
        return (
            <React.Fragment>
                <div className = "Lists">
                {this.props.lists.map((list, key) => {
                        return (
                        <Link to={"/lists/" + key} key={key}>
                        <List
                            list={list}
                        />
                        </Link>)
                    })}
                            
                </div>
                <Input type="list" addItem={this.props.addList} onChangeHandler={this.props.onChangeHandler} textInput={this.props.listInput}/>
            </React.Fragment>
        )
    }
}

export default withRouter(Lists);
