import React, {Component} from 'react';
import {Route, Switch, Redirect} from 'react-router-dom';
import './App.css';
import Footer from '../components/Footer/Footer';
import Header from '../components/Header/Header';
import Tasks from '../components/Tasks/Tasks';
import Lists from '../components/Lists/Lists';

class App extends Component {
  state = {
    lists: [
      {
        listName: "Get to work",
        inprogressTasks: [
          "Do Math questions",
          "Leet code problem"
        ],
        completedTasks: [
          "Add lists to React project"
        ]
      },
      {
        listName: "Gym",
        inprogressTasks: [
          "Eat hype pre meal",
          "Go to Gym"
        ],
        completedTasks: [
          "Task 3"
        ]
      }
    ],
    taskInput: ""
  }

  onChangeHandler = (event) => {
    const updatedTaskInput = event.target.value;
    this.setState({taskInput: updatedTaskInput})
  }

  completeTask = (taskIndex, listIndex) => {
    const updatedTasks = this.state.lists;
    updatedTasks[listIndex].completedTasks.push(updatedTasks[listIndex].inprogressTasks[taskIndex])
    updatedTasks[listIndex].inprogressTasks.splice(taskIndex, 1);
    this.setState({tasks: updatedTasks})
  }

  addTask = () => {
    if(/\S/.test(this.state.taskInput)){
      const updatedTasks = this.state.tasks;
      updatedTasks.inprogress.push(this.state.taskInput);
      this.setState({tasks: updatedTasks});
      this.setState({taskInput: ""});
    }
  }

  removeTask = (taskIndex, listIndex) => {
    const updatedTasks = this.state.lists;
    updatedTasks[listIndex].inprogressTasks.push(updatedTasks[listIndex].completedTasks[taskIndex])
    updatedTasks[listIndex].completedTasks.splice(taskIndex, 1);
    this.setState({tasks: updatedTasks})
  }

  deleteTask = (taskIndex, inprogress) => {
    const updatedTasks = this.state.tasks;
    if(inprogress){
      updatedTasks.inprogress.splice(taskIndex, 1);
    }
    else {
      updatedTasks.completed.splice(taskIndex, 1);
    }
    
    this.setState({tasks: updatedTasks})
  }

  render(){
    return (
        <div className="App">
            {console.log("rerendered")}
            <Header page="Tasks"/>
            <Switch>
            <Route path="/lists/:listName" render={(props) => <Tasks
                {...props}
                list={this.state.lists[props.match.params.listName]} 
                completeTask={this.completeTask}
                addTask={this.addTask}
                removeTask={this.removeTask}
                deleteTask={this.deleteTask}
                onChangeHandler={this.onChangeHandler}
                taskInput={this.state.taskInput}
              />} />
              <Route path="/lists" exact render={()=> <Lists lists={this.state.lists} />} />
              <Route path="/test" render={()=> <p>It worked</p>} />
              <Route render={()=> <p>404 Not Found :(</p>} />
            </Switch>
            <Footer />
        </div>
    );
  }
}

export default App;
