import React, {Component} from 'react';
import {withRouter} from 'react-router-dom';
import Task from './Task/Task';

import Input from '../Input/Input';

class Tasks extends Component {
    render(){
        const completedTask = {
            opacity: '0.5'
        }
        return(
            <React.Fragment>

                <h3>Todo ({this.props.list.inprogressTasks.length})</h3>
                {this.props.list.inprogressTasks.map((item, key) => {
                    return(<Task
                        style={{opacity: '1'}}
                        key={key}
                        name={item}
                        clicked={() => this.props.completeTask(key)}
                        deleteTask={() => this.props.deleteTask(key, true)}
                    />);
                })}

                <br />
                <br />

                <h3>Completed ({this.props.lists.completedTasks.length})</h3>
                {this.props.lists.completedTasks.map((item, key) => {
                    return(<Task
                        {...props}
                        style={completedTask}
                        key={key} 
                        name={item} 
                        clicked={() => this.props.removeTask(key)}
                        deleteTask={() => this.props.deleteTask(key, false)}
                    />);
                })}

                <Input addTask={this.props.addTask} onChangeHandler={this.props.onChangeHandler} taskInput={this.props.taskInput} />
            </React.Fragment>
        )
    }
}

export default withRouter(Tasks);
